.watermark {
    position: fixed;
    top: 50%;
    left: 50%;
    transform: translate(-50%, -50%) rotate(-45deg);
    opacity: 0.3;
    font-size: 100px;
    color: rgba(255, 0, 0, 0.3);
}




.days_tab {
    position: relative;
}

.barchart-Wrapper {
      display: table;
      position: relative;
      margin: 20px 0;
      height: 120px;
    }
    .barChart-Container {
      display: table-cell;
      width: 100%;
      height: 100%;
      padding-left: 20px;
      padding-right: 20px;
    }
    .barchart {
     display: table;
     table-layout: fixed;
     height: 100%;
     width: 100%;
     border-bottom: 3px solid darkgrey;
    }
    .barchart-Col {
      position: relative;
      vertical-align: bottom;
      display: table-cell;
      height: 100%;
    }
    .barchart-Col + .barchart-Col {
      border-left: 4px solid transparent;
    }
    .barchart-Bar {
      position: static;
      height: 0;
      transition: height 0.5s 2s;
      width: 10px;
      margin: auto;
    }
    .barchart-Bar:after {
      content: attr(attr-height);
      color: white;
      position: absolute;
      text-align: center;
      width: 100%;
    }
    .barchart-BarFooter {
      position: absolute;
      text-align: center;
      left:40px;
      height: 10%;
      width: 100%;
    }
    .barchart-BarFooter h3 {
      color: darkred;
    }
    .barchart-TimeCol {
      position: absolute;
      top: 0;
      height: 100%;
      width: 100%;
    }
    .barchart-Time {
      height: 25%;
      vertical-align: middle;
      position: relative;
    }
    .barchart-Time:after {
      border-bottom: 3px solid white;
      content: "";
      position: absolute;
      width: 100%;
      left: 0;
      top: 0em;
    }
    .barchart-TimeText {
      position: absolute;
      top: -8px;
      z-index: 1;
      background: white;
      padding-right: 5px;
      color: #4d4d4d;
      font-size: 15px;
      font-family: 'Avenir Medium';
    }
    .sq1-bar {
      background-color: #3489eb;
    }
    .rq1-bar {
      background-color: #a61a17;
    }
    .sq2-bar {
      background-color: #3489eb;
    }
    .rq2-bar {
      background-color: #a61a17;
    }
    .sq3-bar {
      background-color: #3489eb;
    }
    .rq3-bar {
      background-color: #a61a17;
    }
    .sq4-bar {
      background-color: #3489eb;
    }
    .rq4-bar {
      background-color: #a61a17;
    }

